sect2.example:
   Création d'un fantôme paresseux


<?php
class Example
{
    public function __construct(public int $prop)
    {
        echo __METHOD__, "\n";
    }
}

$reflector = new ReflectionClass(Example::class);
$lazyObject = $reflector->newLazyGhost(function (Example $object) {
    // Initialise l'objet sur place
    $object->__construct(1);
});

var_dump($lazyObject);
var_dump(get_class($lazyObject));

// Déclenche l'initialisation
var_dump($lazyObject->prop);
?>


   &example.outputs;


lazy ghost object(Example)#3 (0) {
["prop"]=>
uninitialized(int)
}
string(7) "Example"
Example::__construct
int(1)

   Création d'un proxy paresseux


<?php
class Example
{
    public function __construct(public int $prop)
    {
        echo __METHOD__, "\n";
    }
}

$reflector = new ReflectionClass(Example::class);
$lazyObject = $reflector->newLazyProxy(function (Example $object) {
    // Crée et retourne l'instance réelle
    return new Example(1);
});

var_dump($lazyObject);
var_dump(get_class($lazyObject));

// Déclenche l'initialisation
var_dump($lazyObject->prop);
?>


   &example.outputs;


lazy proxy object(Example)#3 (0) {
  ["prop"]=>
  uninitialized(int)
}
string(7) "Example"
Example::__construct
int(1)
