refentry.refsect1:
&reftitle.returnvalues;


 Retourne la nouvelle fermeture sous la forme d'un objet Closure,
 ou &null; en cas d'erreur.

  &reftitle.examples;

   Exemple Closure::bindTo


<?php

class A
{
    private $val;

    public function __construct($val)
    {
        $this->val = $val;
    }

    public function getClosure()
    {
        //Retourne une fermeture liée à cet objet et ce contexte
        return function() {
            return $this->val;
        };
    }
}

$ob1 = new A(1);
$ob2 = new A(2);

$cl = $ob1->getClosure();
echo $cl(), "\n";
$cl = $cl->bindTo($ob2);
echo $cl(), "\n";
?>


   &example.outputs.similar;


1
2
