para.example:
      Exemple avec get_class_methods


<?php
class maclasse {
    // constructeur
    function maclasse() {
        return(TRUE);
    }
    // méthode 1
    function myfunc1() {
        return(TRUE);
    }
    // méthode 2
    function mafunc2() {
        return(TRUE);
    }
}
$ma_classe = new maclasse();

$class_methods = get_class_methods(get_class($ma_classe));

foreach ($class_methods as $method_name) {
    echo "$method_name\n";
}
?>


    &example.outputs;


maclasse
myfunc1
mafunc2
