refentry.refsect1:
&reftitle.description;
arrayget_class_vars stringclass
Retourne les valeurs par défaut des propriétés d'une classe.

&reftitle.parameters;




   class



     Le nom de la classe

  &reftitle.examples;



    Exemple avec get_class_vars


<?php

class MyClass
{
    public $var1; // Ceci n'a pas de valeur par défaut explicite (techniquement NULL par défaut)...
    public $var2 = "xyz";
    public $var3 = 100;
    private $var4;

    public function __construct()
    {
        // changement de quelques propriétés
        $this->var1 = "foo";
        $this->var2 = "bar";
        return true;
    }

}

$my_class = new MyClass();

$class_vars = get_class_vars(get_class($my_class));

foreach ($class_vars as $name => $value) {
     echo "{$name}: ", var_export($value, true), "\n";
}

?>


    &example.outputs;


var1: NULL
var2: 'xyz'
var3: 100







    Exemple avec get_class_vars et les contextes


<?php

function format($array)
{
    return implode('|', array_keys($array)) . "\r\n";
}

class TestCase
{
    public $a = 1;
    protected $b = 2;
    private $c = 3;

    public static function expose()
    {
        echo format(get_class_vars(__CLASS__));
    }
}

TestCase::expose();
echo format(get_class_vars('TestCase'));

?>


    &example.outputs;


// 5.0.0
a| * b| TestCase c
a| * b| TestCase c

// 5.0.1 - 5.0.2
a|b|c
a|b|c

// 5.0.3 +
a|b|c
a
