  &reftitle.examples;

   Exemple avec EventBufferEvent::connectHost


<?php
/* Fonction de rappel de lecture */
function readcb($bev, $base) {
    //$input = $bev->input; //$bev->getInput();

    //$pos = $input->search("TTP");
    $pos = $bev->input->search("TTP");

    while (($n = $bev->input->remove($buf, 1024)) > 0) {
        echo $buf;
    }
}

/* Fonction de rappel de l'événement */
function eventcb($bev, $events, $base) {
    if ($events & EventBufferEvent::CONNECTED) {
        echo "Connecté.\n";
    } elseif ($events & (EventBufferEvent::ERROR | EventBufferEvent::EOF)) {
        if ($events & EventBufferEvent::ERROR) {
            echo "Erreur DNS : ", $bev->getDnsErrorString(), PHP_EOL;
        }

        echo "Fermeture\n";
        $base->exit();
        exit("Fait !\n");
    }
}

$base = new EventBase();

$dns_base = new EventDnsBase($base, TRUE); // Résolution DNS asynchrone
if (!$dns_base) {
    exit("Échec de l'initialisation de la base DNS\n");
}

$bev = new EventBufferEvent($base, /* utilisation d'un socket interne */ NULL,
    EventBufferEvent::OPT_CLOSE_ON_FREE | EventBufferEvent::OPT_DEFER_CALLBACKS,
    "readcb", /* writecb */ NULL, "eventcb", $base
);
if (!$bev) {
    exit("Échec lors de la création du socket bufferevent\n");
}

//$bev->setCallbacks("readcb", /* writecb */ NULL, "eventcb", $base);
$bev->enable(Event::READ | Event::WRITE);

$output = $bev->output; //$bev->getOutput();
if (!$output->add(
    "GET {$argv[2]} HTTP/1.0\r\n".
    "Host: {$argv[1]}\r\n".
    "Connection: Close\r\n\r\n"
)) {
    exit("Échec lors de l'ajout de la demande dans le tampon de sortie\n");
}

if (!$bev->connectHost($dns_base, $argv[1], 80, EventUtil::AF_UNSPEC)) {
    exit("Impossible de se connecter à l'hôte {$argv[1]}\n");
}

$base->dispatch();
?>


