refentry.refsect1:
  &reftitle.examples;

   Exemple avec EventBufferEvent::getOutput


<?php
$base = new EventBase();

$dns_base = new EventDnsBase($base, TRUE); // Utilisation de la résolution async DNS
if (!$dns_base) {
    exit("Échec lors de l'initialisation de la base DNS\n");
}

$bev = new EventBufferEvent($base, /* use internal socket */ NULL,
    EventBufferEvent::OPT_CLOSE_ON_FREE | EventBufferEvent::OPT_DEFER_CALLBACKS,
    "readcb", /* writecb */ NULL, "eventcb", $base
);
if (!$bev) {
    exit("Échec lors de la création du socket bufferevent\n");
}

$bev->enable(Event::READ | Event::WRITE);

$output = $bev->getOutput();
if (!$output->add(
    "GET {$argv[2]} HTTP/1.0\r\n".
    "Host: {$argv[1]}\r\n".
    "Connection: Close\r\n\r\n"
)) {
    exit("Échec lors de l'ajout de la demande dans le tampon de sortie\n");
}

/* ... */
?>
