  &reftitle.examples;



    Exemple avec hash_file


<?php
/* Crée un fichier pour calculer son empreinte numérique */
file_put_contents('example.txt', 'The quick brown fox jumped over the lazy dog.');

echo hash_file('sha256', 'example.txt');
?>


