&reftitle.description;
stringfalseini_get stringoption
Retourne la valeur de l'option de configuration varname en cas de succès.

&reftitle.parameters;




   option



     Le nom de l'option de configuration.

&reftitle.returnvalues;


 Retourne la valeur de l'option de configuration
 varname en cas de succès, ou une
 &string; vide pour les valeurs &null;. Retourne &false;
 si l'option de configuration n'existe pas.

  &reftitle.examples;



    Exemples avec ini_get


<?php

/*
Notre fichier php.ini contient les directives suivantes :

display_errors = On
register_globals = Off
post_max_size = 8M
*/

echo 'display_errors = ' . ini_get('display_errors') . "\n";
echo 'register_globals = ' . (int) ini_get('register_globals') . "\n";
echo 'post_max_size = ' . ini_get('post_max_size') . "\n";
echo 'post_max_size + 1 = ' . (rtrim(ini_get('post_max_size'), 'KMG') + 1) . "\n";
echo 'post_max_size in bytes = ' . return_bytes(ini_get('post_max_size'));

function return_bytes($val)
{
    $val = trim($val);
    $num = (int) rtrim($val, 'KMG');
    $last = strtolower($val[strlen($val) - 1]);

    switch ($last) {
        // Le modifieur 'G' est disponible
        case 'g':
            $num = $num * 1024 * 1024 * 1024;
            break;
        case 'm':
            $num = $num * 1024 * 1024;
            break;
        case 'k':
            $num *= 1024;
            break;
    }

    return $num;
}

?>


    &example.outputs.similar;



