refentry.refsect1:
  &reftitle.examples;



    Exécution d'une requête SQL sans buffer sur Ingres


<?php
$link = ingres_connect("demodb");

$result = ingres_unbuffered_query($link, "select * from user_profile");
while ($row = ingres_fetch_row($result)) {
    echo $row[1];
    echo $row[2];
}
?>




    Passage de paramètres de requêtes à ingres_unbuffered_query


<?php
$link = ingres_connect("demodb");

$params[] = "Emma";
$query = "select * from user_profile where up_first = ?";
$result = ingres_unbuffered_query($link, $query, $params);
while ($row = ingres_fetch_row($result)) {
    echo $row[1];
    echo $row[2];
}
?>




    Insertion d'un BLOB dans les paramètres de requêtes SQL Ingres


<?php
$link = ingres_connect("demodb");

//Ouverture d'une photo
$fh = fopen("photo.jpg","r");
$blob_data = stream_get_contents($fh);
fclose($fh);

//Préparation des parametres
$params[] = $blob_data;
$params[] = 1201;

//Définition des types de paramètres
$param_types = "Bi";

$query = "update user_profile set up_image = ? where up_id = ?";
$result = ingres_unbuffered_query($link, $query , $params, $param_types);

if (ingres_errno())
{
    echo ingres_errno() . " : " . ingres_error() . "\n";
}
?>
