refentry.refsect1:
  &reftitle.examples;

   MongoDB\Driver\WriteResult::getWriteErrors avec une seule erreur


<?php

$manager = new MongoDB\Driver\Manager;

/* Par défaut, les opérations d'écriture en bloc sont exécutées en série dans 
 * l'ordre et l'exécution s'arrêtera après la première erreur.
 */
$bulk = new MongoDB\Driver\BulkWrite;
$bulk->insert(['_id' => 1]);
$bulk->insert(['_id' => 2]);
$bulk->insert(['_id' => 2]);
$bulk->insert(['_id' => 3]);
$bulk->insert(['_id' => 4]);
$bulk->insert(['_id' => 4]);

try {
    $result = $manager->executeBulkWrite('db.collection', $bulk);
} catch (MongoDB\Driver\Exception\BulkWriteException $e) {
    var_dump($e->getWriteResult()->getWriteErrors());
}


?>


   &example.outputs.similar;


array(1) {
  [0]=>
  object(MongoDB\Driver\WriteError)#5 (4) {
    ["message"]=>
    string(81) "E11000 duplicate key error collection: db.collection index: _id_ dup key: { : 2 }"
    ["code"]=>
    int(11000)
    ["index"]=>
    int(2)
    ["info"]=>
    NULL
  }
}




   MongoDB\Driver\WriteResult::getWriteErrors avec plusieurs erreurs


<?php

$manager = new MongoDB\Driver\Manager;

/* L'option "ordered" peut être utilisée pour permettre aux opérations 
 * d'écriture en bloc de continuer à s'exécuter après la première erreur.
 */
$bulk = new MongoDB\Driver\BulkWrite(['ordered' => false]);
$bulk->insert(['_id' => 1]);
$bulk->insert(['_id' => 2]);
$bulk->insert(['_id' => 2]);
$bulk->insert(['_id' => 3]);
$bulk->insert(['_id' => 4]);
$bulk->insert(['_id' => 4]);

try {
    $result = $manager->executeBulkWrite('db.collection', $bulk);
} catch (MongoDB\Driver\Exception\BulkWriteException $e) {
    var_dump($e->getWriteResult()->getWriteErrors());
}

?>


   &example.outputs.similar;


array(2) {
  [0]=>
