refentry.refsect1:
    &reftitle.examples;

     Style orienté objet


<?php
$mysqli = new mysqli("localhost", "utilisateur", "mot_de_passe", "base");

/* Vérification de la connexion */
if (mysqli_connect_errno()) {
    printf("Connexion échouée : %s\n", mysqli_connect_error());
    exit();
}

$mysqli->query("CREATE TABLE ma_Ville LIKE Ville");

/* Préparation de la commande d'insertion */
$query = "INSERT INTO ma_ville (Nom, Pays, Region) VALUES (?,?,?)";
$stmt = $mysqli->prepare($query);

$stmt->bind_param("sss", $val1, $val2, $val3);

$val1 = 'Stuttgart';
$val2 = 'DEU';
$val3 = 'Baden-Wuerttemberg';

/* Exécution de la commande */
$stmt->execute();

$val1 = 'Bordeaux';
$val2 = 'FRA';
$val3 = 'Aquitaine';

/* Exécution de la commande */
$stmt->execute();

/* Fermeture de la commande */
$stmt->close();

/* Lit toutes les données de ma_ville */
$query = "SELECT Nom, Pays, Region FROM ma_ville";
if ($result = $mysqli->query($query)) {
    while ($row = $result->fetch_row()) {
        printf("%s (%s,%s)\n", $row[0], $row[1], $row[2]);
    }
    /* Libère de résultat */
    $result->close();
}

/* Suppression de la table */
$mysqli->query("DROP TABLE ma_ville");

/* Fermeture de la connexion */    
$mysqli->close(); 
?>




     Style procédural


<?php
$link = mysqli_connect("localhost", "utilisateur", "mot_de_passe", "base");

/* Vérification de la connexion */
if (mysqli_connect_errno()) {
    printf("Connexion échouée : %s\n", mysqli_connect_error());
    exit();
}

mysqli_query($link, "CREATE TABLE ma_ville LIKE Ville");

/* Préparation de la commande d'insertion */
$query = "INSERT INTO ma_ville (Nom, Pays, Region) VALUES (?,?,?)";
$stmt = mysqli_prepare($link, $query);

mysqli_stmt_bind_param($stmt, "sss", $val1, $val2, $val3);

$val1 = 'Stuttgart';
$val2 = 'DEU';
$val3 = 'Baden-Wuerttemberg';

/* Exécution de la commande */
mysqli_stmt_execute($stmt);

$val1 = 'Bordeaux';
$val2 = 'FRA';
$val3 = 'Aquitaine';

/* Exécution de la commande */
mysqli_stmt_execute($stmt);

/* Fermeture de la commande */
mysqli_stmt_close($stmt);

/* Lit toutes les données de ma_ville */
$query = "SELECT Nom, Pays, Region FROM ma_ville";
if ($result = mysqli_query($link, $query)) {
    while ($row = mysqli_fetch_row($result)) {
        printf("%s (%s,%s)\n", $row[0], $row[1], $row[2]);
    }
    /* Libère de résultat */
    mysqli_free_result($result);
}

/* Suppression de la table */
mysqli_query($link, "DROP TABLE ma_ville");

/* Fermeture de la connexion */    
mysqli_close($link); 
?>



    &example.outputs;


Stuttgart (DEU,Baden-Wuerttemberg)
Bordeaux (FRA,Aquitaine)
