refentry.refsect1:
  &reftitle.description;

&style.oop;

   public objectfalsemysqli_result::fetch_field



&style.procedural;

   objectfalsemysqli_fetch_field
   mysqli_resultresult



   Retourne les attributs de la prochaine colonne dans le jeu de
   résultats représenté par le paramètre result
   en tant qu'objet. Appelez cette fonction de façon répétitive pour
   récupérer les informations de toutes les colonnes.

  &reftitle.examples;

   &style.oop;


<?php
$mysqli = new mysqli("localhost", "my_user", "my_password", "world");

/* Vérification de la connexion */
if (mysqli_connect_errno()) {
    printf("Échec de la connexion : %s\n", mysqli_connect_error());
    exit();
}

$query = "SELECT Name, SurfaceArea from Country ORDER BY Code LIMIT 5";

if ($result = $mysqli->query($query)) {

    /* Récupère les informations d'un champ pour toutes les colonnes */
    while ($finfo = $result->fetch_field()) {

        printf("Name:     %s\n", $finfo->name);
        printf("Table:    %s\n", $finfo->table);
        printf("max. Len: %d\n", $finfo->max_length);
        printf("Flags:    %d\n", $finfo->flags);
        printf("Type:     %d\n\n", $finfo->type);
    }
    $result->close();
}

/* Fermeture de la connexion */
$mysqli->close();
?>




   &style.procedural;


<?php
$link = mysqli_connect("localhost", "my_user", "my_password", "world");

/* Vérification de la connexion */
if (mysqli_connect_errno()) {
    printf("Échec de la connexion : %s\n", mysqli_connect_error());
    exit();
}

$query = "SELECT Name, SurfaceArea from Country ORDER BY Code LIMIT 5";

if ($result = mysqli_query($link, $query)) {

    /* Récupère les informations d'un champ pour toutes les colonnes */
    while ($finfo = mysqli_fetch_field($result)) {

        printf("Name:     %s\n", $finfo->name);
        printf("Table:    %s\n", $finfo->table);
        printf("max. Len: %d\n", $finfo->max_length);
        printf("Flags:    %d\n", $finfo->flags);
        printf("Type:     %d\n\n", $finfo->type);
    }
    mysqli_free_result($result);
}

/* Fermeture de la connexion */
mysqli_close($link);
?>


   &examples.outputs;


Name:     Name
Table:    Country
max. Len: 11
Flags:    1
Type:     254

