refentry.refsect1:
  &reftitle.description;

&style.oop;

   public objectmysqli_result::fetch_field



&style.procedural;

   objectmysqli_fetch_field
   mysqli_resultresult




   Cette fonction fonctionne comme mysqli_fetch_field
   à la différence que, au lieu de retourner un objet à la fois
   pour chaque champ, les colonnes sont retournées en tant
   que tableau d'objets.

  &reftitle.examples;

   &style.oop;


<?php
$mysqli = new mysqli("127.0.0.1", "root", "foofoo", "sakila");

/* vérification de la connexion */
if ($mysqli->connect_errno) {
    printf("Échec de la connexion : %s\n", $mysqli->connect_error);
    exit();
}

foreach (array('latin1', 'utf8') as $charset) {

    // Définir le jeu de caractère, pour montrer son impact sur certaines valeurs (par exemple, longeurs en octets)
    $mysqli->set_charset($charset);

    $query = "SELECT actor_id, last_name from actor ORDER BY actor_id";

    echo "============================\n";
    echo "Jeu de caractères : $charset\n";
    echo "============================\n";

    if ($result = $mysqli->query($query)) {

        /* Récupère les informations des champs pour toutes les colonnes */
        $finfo = $result->fetch_fields();

        foreach ($finfo as $val) {
            printf("Name:      %s\n",   $val->name);
            printf("Table:     %s\n",   $val->table);
            printf("Max. Len:  %d\n",   $val->max_length);
            printf("Length:    %d\n",   $val->length);
            printf("charsetnr: %d\n",   $val->charsetnr);
            printf("Flags:     %d\n",   $val->flags);
            printf("Type:      %d\n\n", $val->type);
        }
        $result->free();
    }
}
$mysqli->close();
?>




   &style.procedural;


<?php
$link = mysqli_connect("127.0.0.1", "my_user", "my_password", "sakila");

/* vérification de la connexion */
if (mysqli_connect_errno()) {
    printf("Échec de la connexion : %s\n", mysqli_connect_error());
    exit();
}

foreach (array('latin1', 'utf8') as $charset) {

    // Définir le jeu de caractère, pour montrer son impact sur certaines valeurs (par exemple, longeurs en octets)
    mysqli_set_charset($link, $charset);

    $query = "SELECT actor_id, last_name from actor ORDER BY actor_id";

    echo "============================\n";
    echo "Jeu de caractères : $charset\n";
    echo "============================\n";

    if ($result = mysqli_query($link, $query)) {

        /* Récupère les informations des champs pour toutes les colonnes */
        $finfo = mysqli_fetch_fields($result);

        foreach ($finfo as $val) {
            printf("Name:      %s\n",   $val->name);
            printf("Table:     %s\n",   $val->table);
            printf("Max. Len:  %d\n",   $val->max_length);
            printf("Length:    %d\n",   $val->length);
            printf("charsetnr: %d\n",   $val->charsetnr);
            printf("Flags:     %d\n",   $val->flags);
            printf("Type:      %d\n\n", $val->type);
        }
        mysqli_free_result($result);
    }
}

mysqli_close($link);
?>


