$host="p:localhost";
$port=3306;
$socket="/tmp/mysql.sock";
$user="root";
$password="password";
$dbname="test";

$conn = new mysqli($host, $user, $password, $dbname, $port, $socket)
   or die ('On ne peut pas se connecter au serveur de base de données' . mysqli_connect_error());
