<?php /* Activation de la collection des statistiques - par défaut : désactivé */ ini_set("mysqlnd_qc.collect_statistics", 1);
/* Activation de la collection de toutes les durées relatives aux statistiques - par défaut : activé mais écrasé par la directive mysqlnd_qc.collect_statistics = 0 */ ini_set("mysqlnd_qc.collect_time_statistics", 1);
/* Population du cache, e.g. en utilisant mysqli */ $mysqli = new mysqli('host', 'user', 'password', 'schema');
/* Cache miss et cache put */ $mysqli->query("/*qc=on*/SELECT id FROM test"); /* Accès au cache */ $mysqli->query("/*qc=on*/SELECT id FROM test");
/* Affichage des statistiques du cœur */ var_dump(mysqlnd_qc_get_core_stats()); ?>
