refentry.refsect1:
&reftitle.parameters;




   username



     Le nom d'utilisateur Oracle.




   password



     Le mot de passe de l'utilisateur.




   connection_string

    &oci.db;



   encoding

    &oci.charset;



   session_mode

    &oci.sessionmode;

  &reftitle.examples;



    Exemple avec oci_pconnect


<?php

// Connexion au service XE (i.e. base de données) sur la machine "localhost"
$conn = oci_pconnect('hr', 'welcome', 'localhost/XE');
if (!$conn) {
    $e = oci_error();
    trigger_error(htmlentities($e['message'], ENT_QUOTES), E_USER_ERROR);
}

$stid = oci_parse($conn, 'SELECT * FROM employees');
oci_execute($stid);

echo "<table border='1'>\n";
while ($row = oci_fetch_array($stid, OCI_ASSOC+OCI_RETURN_NULLS)) {
    echo "<tr>\n";
    foreach ($row as $item) {
        echo "    <td>" . ($item !== null ? htmlentities($item, ENT_QUOTES) : "") . "</td>\n";
    }
    echo "</tr>\n";
}
echo "</table>\n";

?>






   Voir la fonction oci_connectpour plus d'exemples sur l'utilisation
   de ce paramètre.
