refentry.refsect1:
&reftitle.parameters;




   connection

    &oci.parameter.connection;



   action



     Chaîne choisie par l'utilisateur jusqu'à 32 octets de
     long.

  &reftitle.examples;



    Définition d'une action


<?php

$c = oci_connect('hr', 'welcome', 'localhost/XE');

// Enregistre l'action
oci_set_action($c, 'Friend Lookup');

// Code générant un aller-retour (round-trip), par exemple, une requête :
$s = oci_parse($c, 'select * from dual');
oci_execute($s);
oci_fetch_all($s, $res);

sleep(30);

?>




// Pendant l'exécution du script, l'administrateur peut voir les actions
// en cours d'exécution :

sqlplus system/welcome
SQL> select action from v$session;
