refentry.refsect1:
&reftitle.description;
intovrimos_prepare intconnection_id stringquery
Prépare une requête SQL Ovrimos.

&reftitle.returnvalues;


 Retourne un identifiant de résultat en cas de succès, ou
 &false; si une erreur survient.

  &reftitle.examples;



    Exemple avec ovrimos_prepare


<?php
$conn=ovrimos_connect("db_host", "8001", "admin", "password");
if ($conn!=0) {
    echo "Connexion ok!";

    // Prepare the statement
    $res=ovrimos_prepare($conn, "select table_id, table_name
                                  from sys.tables where table_id=1");
    if ($res != 0) {
        echo "Préparation ok!";
        // Execute the prepared statement
        if (ovrimos_execute($res)) {
            echo "Exécution ok!\n";
            ovrimos_result_all($res);
        } else {
            echo "Erreur lors de l'exécution !";
        }
        ovrimos_free_result($res);
    } else {
        echo "Erreur lors de la préparation !\n";
    }
    ovrimos_close($conn);
}
?>
