refentry.refsect1:
&reftitle.description;
intovrimos_result_all intresult_id stringformat
Affiche un résultat Ovrimos sous forme de tableau HTML.

  &reftitle.examples;



    Prépare une requête, l'exécute sur Ovrimos et affiche le résultat


<?php
$conn = ovrimos_connect("db_host", "8001", "admin", "password");
if ($conn != 0) {
    echo "Connexion ok!";
    $res = ovrimos_prepare($conn, "select table_id, table_name
                                    from sys.tables where table_id = 7");
    if ($res != 0) {
        echo "Préparation ok!";
        if (ovrimos_execute($res, array(3))) {
            echo "Exécution ok!\n";
            ovrimos_result_all($res);
        } else {
            echo "Erreur lors de l'exécution !";
        }
        ovrimos_free_result($res);
    } else {
        echo "Erreur lors de la préparation !\n";
    }
    ovrimos_close($conn);
}
?>







    ovrimos_result_all avec meta-information


<?php
$conn = ovrimos_connect("db_host", "8001", "admin", "password");
if ($conn != 0) {
    echo "Connexion ok!";
    $res = ovrimos_exec($conn, "select table_id, table_name
                                 from sys.tables where table_id = 1");
    if ($res != 0) {
        echo "Requête ok! curseur=" . ovrimos_cursor($res) . "\n";
        $colnb = ovrimos_num_fields($res);
        echo "Colonnes affichées=" . $colnb . "\n";
        for ($i=1; $i <= $colnb; $i++) {
            $name = ovrimos_field_name($res, $i);
            $type = ovrimos_field_type($res, $i);
            $len = ovrimos_field_len($res, $i);
            echo "Colonne " . $i . " nom=" . $name . " type=" . $type . " taille=" . $len . "\n";
        }
        ovrimos_result_all($res);
        ovrimos_free_result($res);
    }
    ovrimos_close($conn);
}
?>
