  &reftitle.examples;



    Un exemple avec PharData::__construct




<?php
try {
    $p = new PharData('/path/to/my.tar', Phar::CURRENT_AS_FILEINFO | Phar::KEY_AS_FILENAME);
} catch (UnexpectedValueException $e) {
    die('Ne peut pas ouvrir mon.tar');
} catch (BadMethodCallException $e) {
    echo 'techniquement, ça ne peut pas arriver';
}
echo file_get_contents('phar:///chemin/vers/mon.tar/exemple.txt');
?>
