refentry.refsect1:
&reftitle.description;
voidprinter_draw_roundrect resourceprinter_handle intul_x intul_y intlr_x intlr_y intwidth intheight
Dessine un rectangle, avec des coins arrondis.

  &reftitle.examples;



    Exemple avec printer_draw_roundrect


<?php
$handle = printer_open();
printer_start_doc($handle, "Mon Document");
printer_start_page($handle);

$pen = printer_create_pen(PRINTER_PEN_SOLID, 2, "000000");
printer_select_pen($handle, $pen);

$brush = printer_create_brush(PRINTER_BRUSH_SOLID, "2222FF");
printer_select_brush($handle, $brush);

printer_draw_roundrect($handle, 1, 1, 500, 500, 200, 200);

printer_delete_brush($brush);
printer_delete_pen($pen);

printer_end_page($handle);
printer_end_doc($handle);
printer_close($handle);
?>
