    Verrouillage du stockage Thread


<?php
class My extends Thread {
    public function run() {
        var_dump($this->lock());
        /** personne ne peut lire ou écrire **/
        var_dump($this->unlock());
        /** Reprise de la lecture/écriture pour tous les autres contextes */
    }
}
$my = new My();
$my->start();
?>


