refentry.refsect1:
&reftitle.description;
stringradius_cvt_string stringdata
Convertit des données brutes en chaîne de caractères

  &reftitle.examples;



    Exemple avec radius_cvt_string


<?php
while ($resa = radius_get_attr($res)) {

    if (!is_array($resa)) {
        printf ("Erreur lors de la récupération des attributs : %s\n",  radius_strerror($res));
        exit;
    }

    $attr = $resa['attr'];
    $data = $resa['data'];

    switch ($attr) {

    case RADIUS_FILTER_ID:
        $id = radius_cvt_string($data);
        echo "Filtre ID : $id<br>\n";
        break;
    }
}
?>
