<?php
$rar_arch = RarArchive::open('solid.rar');
if ($rar_arch === FALSE)
    die("Impossible d'ouvrir l'archive RAR.");
$rar_entry = $rar_arch->getEntry('tese.txt');
if ($rar_entry === FALSE)
    die("Impossible de récupérer cette entrée.");
echo get_class($rar_entry)."\n";
echo $rar_entry;
$rar_arch->close();
?>


