    Exemple avec spl_autoload_register


<?php

namespace Foobar;

class Foo {
    static public function test($name) {
        print '[['. $name .']]';
    }
}

spl_autoload_register(__NAMESPACE__ .'::Foo::test'); // Depuis PHP 5.3.0

new InexistentClass;

?>


