refentry.refsect1:
&reftitle.description;

 public booltidyNode::hasSiblings




 Indique si le nœud courant a des nœuds frères.

  &reftitle.examples;



    Exemple avec tidyNode::hasSiblings


<?php

$html = <<< HTML
<html><head>
<?php echo '<title>title</title>'; ?>
<# 
  /* code JSTE  */
  alert('Hello World'); 
#>
</head>
<body>

<?php
  // code PHP 
  echo 'hello world!';
?>

<%
  /* code ASP  */
  response.write("Hello World!")
%>

<!-- Comments -->
Hello World
</body></html>
Outside HTML
HTML;


$tidy = tidy_parse_string($html);
$num = 0;

// La balise html
var_dump($tidy->html()->hasSiblings());

// La balise head
var_dump($tidy->html()->child[0]->hasSiblings());

?>


    &example.outputs;


bool(false)
bool(true)
