refentry.refsect1:
&reftitle.description;

 public booltidyNode::isAsp




 Vérifie si le nœud est du code ASP.

  &reftitle.examples;



    Extrait du code ASP depuis un document HTML mixte


<?php

$html = <<< HTML
<html><head>
<?php echo '<title>title</title>'; ?>
<# 
  /* code JSTE */
  alert('Hello World'); 
#>
</head>
<body>

<?php
  // code PHP
  echo 'hello world!';
?>

<%
  /* code ASP */
  response.write("Hello World!")
%>

<!-- Comments -->
Hello World
</body></html>
Outside HTML
HTML;


$tidy = tidy_parse_string($html);
$num = 0;

get_nodes($tidy->html());

function get_nodes($node) {

    // Vérifie si le nœud courant est du type requis
    if($node->{"isAsp()) {
        echo "\n\n# Noeud $node_type #" . ++$GLOBALS['num'] . "\n";
        echo $node->value;
    }

    // Vérifie si le nœud courant a des enfants
    if($node->hasChildren()) {
        foreach($node->child as $child) {
            get_nodes($child);
        }
    }
}

?>


    &example.outputs;


# Noeud asp #1
<%
  /* code ASP */
  response.write("Hello World!")
%>
