refentry.refsect1:
&reftitle.description;

 public booltidyNode::isPhp




 Indique si un nœud contient du code PHP.

&reftitle.returnvalues;


 Retourne &true; si le nœud courant est du code PHP, 
 &false; sinon.

  &reftitle.examples;



    Récupération de code PHP dans un document HTML


<?php

$html = <<< HTML
<html><head>
<?php echo '<title>title</title>'; ?>
<# 
  /* code JSTE */
  alert('Hello World'); 
#>
</head>
<body>

<?php
  // code PHP
  echo 'hello world!';
?>

<%
  /* code ASP */
  response.write("Hello World!")
%>

<!-- Comments -->
Hello World
</body></html>
Outside HTML
HTML;


$tidy = tidy_parse_string($html);
$num = 0;

get_nodes($tidy->html());

function get_nodes($node) {

    // Vérifie si le nœud courant est du type demandé
    if($node->{"isPhp()) {
        echo "\n\n# Noeud php #" . ++$GLOBALS['num'] . "\n";
        echo $node->value;
    }

    // Vérifie si le nœud courant a des enfants
    if($node->hasChildren()) {
        foreach($node->child as $child) {
            get_nodes($child);
        }
    }
}

?>


    &example.outputs;


# Noeud PHP #1
<?php echo '<title>Titre</title>'; ?>

# Noeud PHP #2
<?php
  // code PHP
  echo 'hello world!';
?>
