refentry.refsect1:
&reftitle.returnvalues;


 Retourne &true; si var est un &integer;, 
 &false; sinon.

  &reftitle.examples;



    Exemple avec is_int


<?php
if (is_int(23)) {
    echo "est un entier\n";
} else {
    echo "n'est pas un entier\n";
}
var_dump(is_int(23));
var_dump(is_int("23"));
var_dump(is_int(23.5));
var_dump(is_int(true));
?>


    &example.outputs;


est un entier
bool(true)
bool(false)
bool(false)
bool(false)
