refentry.refsect1:
  &reftitle.examples;



    Exemple avec wincache_ucache_set et
     le paramètre key sous forme de &string;


<?php
$bar = 'BAR';
var_dump(wincache_ucache_set('foo', $bar));
var_dump(wincache_ucache_get('foo'));
$bar1 = 'BAR1';
var_dump(wincache_ucache_set('foo', $bar1));
var_dump(wincache_ucache_get('foo'));
?>


    &example.outputs;


bool(true)
string(3) "BAR"
bool(true)
string(3) "BAR1"







    Exemple avec wincache_ucache_set et
     le paramètre key sous forme de &array;


<?php
$colors_array = array('green' => '5', 'Blue' => '6', 'yellow' => '7', 'cyan' => '8');
var_dump(wincache_ucache_set($colors_array));
var_dump(wincache_ucache_set($colors_array));
var_dump(wincache_ucache_get('Blue'));
?>


    &example.outputs;


array(0) {}
array(0) {}
string(1) "6"
